mapper.select:
SELECT * FROM ( SELECT inner_table.* , ROWNUM AS row_seq FROM ( SELECT post_no, post.user_id as user_id, title, text, thumbnail_image_file, post.reg_date as reg_date, view_count, hashtag, profile_image_file FROM post, users
(title LIKE '%${searchKeyword}%' OR text LIKE '%${searchKeyword}%' OR hashtag LIKE '%${searchKeyword}%') AND flag_pu='UC' AND post.user_id = users.user_id
ORDER BY reg_date DESC ) inner_table WHERE ROWNUM <= #{endRowNum} ) WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}

mapper.update:
UPDATE post
SET title=#{title}, text=#{text}, thumbnail_image_file=#{thumbNailFileName}, reg_date=SYSDATE, lat=#{lat}, lng=#{lng}, hashtag=#{hashtag}
WHERE post_no=#{postNo}

UPDATE post
SET view_count=#{viewCount}
WHERE post_no=#{postNo}
