mapper.update:
UPDATE groups
title = #{title}, text = #{text}, place_name = #{placeName:VARCHAR}, place_detail = #{placeDetail:VARCHAR}, lat = #{lat:VARCHAR}, lng = #{lng:VARCHAR}, meeting_date = #{meetingDate:VARCHAR}
WHERE group_no = #{join.groupNo}

mapper.select:
SELECT * FROM ( SELECT inner_table.* , ROWNUM AS row_seq FROM ( SELECT j.user_id, g.group_no, g.title, g.text, g.view_count, g.place_name, g.place_detail, g.lat, g.lng, g.meeting_date, g.reg_date, profile_image_file FROM groups g, join j, users u WHERE g.group_no = j.group_no AND j.group_role = 1 AND j.user_id = u.user_id
AND (g.title LIKE '%${searchKeyword}%' OR g.text LIKE '%${searchKeyword}%')
ORDER BY g.reg_date DESC ) inner_table WHERE ROWNUM <= #{endRowNum} ) WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}

SELECT COUNT(*) FROM ( SELECT j.user_id, g.group_no, g.title, g.text, g.view_count, g.place_name, g.place_detail, g.lat, g.lng, g.meeting_date, g.reg_date FROM groups g, join j WHERE g.group_no = j.group_no AND j.group_role = 1
AND (g.title LIKE '%${searchKeyword}%' OR g.text LIKE '%${searchKeyword}%')
) countTable
